src/styles/global.qss:
QMainWindow, QMessageBox {
    background-color: @backgroundColor;
}

QAbstractScrollArea #scrollAreaWidgetContents {
    background-color: @backgroundColor;
}

QLabel {
    font-size: 12px;
    color: @textColor;
}

QLineEdit {
    padding: 8px;
    border: none;
    border-radius: 8px;
    background-color: @containerBackgroundColor;
    color: @textColor;
    font-size: 10pt;
}

QTextEdit {
    padding: 8px;
    border: none;
    border-radius: 8px;
    background-color: @containerBackgroundColor;
    color: @textColor;
    font-size: 10pt;
}

QPushButton {
    background-color: #323A7C;
    border: none;
    color: white;
    font-size: 10pt;
    border-radius: 8px;
    padding: 8px;
    outline: none;
}

QPushButton::hover {
    background-color: @backgroundColorPushButton;
}

QComboBox {
    padding: 8px;
    border: none;
    border-radius: 8px;
    background-color: @containerBackgroundColor;
    color: @textColor;
    font-size: 10pt;
}

QComboBox::drop-down {
    border: 0px;
    width: 24px;
}

QComboBox::down-arrow, QSpinBox::down-arrow, QTimeEdit::down-arrow, QDateEdit::down-arrow {
    image: @arrowDownImage;
    width: 8px;
    height: 8px;
}

QComboBox::up-arrow, QSpinBox::up-arrow, QTimeEdit::up-arrow, QDateEdit::up-arrow {
    image: @arrowUpImage;
    width: 8px;
    height: 8px;
}

QComboBox QListView {
    background-color: white;
}

QSpinBox {
    padding: 8px;
    border: none;
    border-radius: 8px;
    background-color: white;
    color: black;
    font-size: 10pt; 
}

QSpinBox::up-button {
    border: none;
    padding-top: 2px;
}

QSpinBox::down-button {
    border: none;
    padding-bottom: 2px;
}

QListWidget {
    border: none;
    border-radius: 8px;
    background-color: white;
    color: black;
    font-size: 10pt;
    padding: 8px;
}

QScrollBar {
    background: transparent;
    margin: 0px;
}

QScrollBar::handle {         
    border: 0px solid red;
    border-radius: 4px;
    background-color: #b6b8bb;
}

QScrollBar::add-line {
    subcontrol-origin: margin;
}

QScrollBar::sub-line {
    subcontrol-origin: margin;
}

QScrollBar:vertical {
    width: 10px;
}

QScrollBar::handle:vertical {         
    min-height: 32px;
}

QScrollBar::add-line:vertical {
    height: 0px;
    subcontrol-position: bottom;
}

QScrollBar::sub-line:vertical {
    height: 0px;
    subcontrol-position: top;
}

QScrollBar:horizontal {
    height: 10px;
}

QScrollBar::handle:horizontal {         
    min-width: 32px;
}

QScrollBar::add-line:horizontal {
    width: 0px;
    subcontrol-position: left;
}

QScrollBar::sub-line:horizontal {
    width: 0px;
    subcontrol-position: right;
}
